    private void jamon1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jamon1ActionPerformed
      //boton de 1 pizza de jamon
        if(cjyq==0){
            //si se presiona se define 1 pizza
          cjyq=1;
          
      }else{
            //si el usuario habia seleccionado 2, se desactiva el boton 2 y se activa el 1
         cjyq=1;
         jamon2.setSelected(false);
      }
        
    }//GEN-LAST:event_jamon1ActionPerformed

