    private void continuarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_continuarActionPerformed
        //boton de continuar
        int comprobar=cP+cjyq+ch+cs+cn+cv;
        
        if (comprobar==0){
        
             JOptionPane.showMessageDialog(rootPane,"ERROR, NO HA ELEGIDO NINGUNA PIZZA!");
            
            
            
            
           
        }else{      
        hawai2.setSelected(false);
        hawai1.setSelected(false);
        jamon2.setSelected(false);
        jamon1.setSelected(false);
        pepe1.setSelected(false);
        pepe2.setSelected(false);
        napo1.setSelected(false);
        napo2.setSelected(false);
        supre2.setSelected(false);
        supre1.setSelected(false); 
        veggi1.setSelected(false);
        veggi2.setSelected(false);
        pedido.cantidades();
        opener.abrirExtras();
        cP=0;
        cjyq=0;
        ch=0;
        cs=0;
        cn=0;
        cv=0;
        this.setVisible(false);
        }

    }//GEN-LAST:event_continuarActionPerformed

